import { useEffect, useState } from "react";
import { Route, Link, Routes, useParams } from 'react-router-dom';
import Book from "../atoms/Book";


const ListBooks = () => {

    const [books, setBooks] = useState([]);
    const [isBooksVisible, SetIsBooksVisible] = useState(true);

    let url = 'https://www.anapioficeandfire.com/api/books';


    // Fetch Tasks
    const fetchBooks = async () => {
        const res = await fetch(url)
        const data = await res.json()

        return data
    }


    useEffect(() => {
        const getTasks = async () => {
            const tasksFromServer = await fetchBooks()
            setBooks(tasksFromServer)

        }

        getTasks()
    }, [])

    function Users() {
        // üëáÔ∏è get ID from url
        SetIsBooksVisible(false)
        const params = useParams();

        console.log(params); // üëâÔ∏è {userId: '4200'}

        return <h2>userId is üëâÔ∏è {params.userId}</h2>;
    }


    return (
        <div>
            {isBooksVisible &&
                <div>
                    <div className="list-books-header-container">
                        <h2>Title:</h2>
                        <h2>Publisher</h2>
                    </div>
                    <div className="list-books-container">{books.map((book) => (
                        <Link to={`/books/${book.name}`} className="list-books-element">
                            <Book book={book} />
                        </Link>
                    ))}</div> </div>}

            <Routes>
                {/* üëáÔ∏è handle dynamic path */}
                <Route path="/books/:userId" element={<Users />} />
            </Routes>
        </div>

    );
}

export default ListBooks;
