const BookInfo = ({ book }) => {

    const url='https://www.anapioficeandfire.com/api/books/'

    const fetchBook = async () => {
        SetIsLoading(true)
        const res = await fetch(`url${book}`)
        const data = await res.json()
        SetIsLoading(false)

        return data
    }

    useEffect(() => {
        const getTasks = async () => {
            const tasksFromServer = await fetchBook()
            setBooks(tasksFromServer)

        }
        getTasks()
    }, [])

    return (
        <div>
            <h2>Title:</h2>
            <p>{book}</p>
        </div>
    );
}

export default BookInfo;
