import { useEffect, useState } from "react";
import { Route, Link, Routes, useParams } from 'react-router-dom';
import Book from "../atoms/Book";


const ListBooks = () => {

    const [books, setBooks] = useState([]);
    const [isLoading, SetIsLoading] = useState(false);


    let url = 'https://www.anapioficeandfire.com/api/books';

    const fetchBooks = async () => {
        SetIsLoading(true)
        const res = await fetch(url)
        const data = await res.json()
        SetIsLoading(false)

        return data
    }


    useEffect(() => {
        const getTasks = async () => {
            const tasksFromServer = await fetchBooks()
            setBooks(tasksFromServer)

        }
        getTasks()
    }, [])

    function BookDetail() {
        const params = useParams();
        return <BookDetail params={params}/>

    }

    function Books() {
        return (
            <div>
                {!isLoading ? <div>
                    <div className="list-books-header-container">
                        <h2>Title:</h2>
                        <h2>Publisher</h2>
                    </div>
                    <div className="list-books-container">{books.map((book) => (
                        <Link to={`/books/${book.name}`} className="list-books-element">
                            <Book book={book} />
                        </Link>
                    ))}
                    </div>
                </div> : <h1>Loading ...</h1>}
            </div>
        )
    }


    return (
        <div>
            <Routes>
                <Route path="/" element={<Books />} />
                <Route path="/books/:bookId" element={<BookDetail />} />
            </Routes>
        </div>

    );
}

export default ListBooks;
