                                {isTitleFilterClicked && <div>
                                    <div onClick={filterBooks(asc)} >Ascending</div>
                                    <div href="#base">Descending</div>
                                </div>}
                            </div>
                        </div>
                        <h2>Publisher</h2>
                    </div>
                    <div className="list-books-container">{books.map((book) => (
                        <Link to={`/books/${book.name}`} className="list-books-element" style={{ textDecoration: 'none' }}>
                            <Book book={book} />
                        </Link>
                    ))}
                    </div>
                </div> : <h1>Loading...</h1>}
            </div>
        )
    }


    return (
        <div>
            <Routes>
                <Route path="/" element={<Books />} />
                <Route path="/books/:userId" element={<BookDetail />} />
            </Routes>
