import { useEffect, useState } from "react";
import { Route, Link, Routes, useParams } from 'react-router-dom';
import Book from "../atoms/Book";
import BookInfo from "../atoms/BookInfo";


const ListBooks = () => {

    const [books, setBooks] = useState([]);
    const [isLoading, SetIsLoading] = useState(false);

    let url = 'https://www.anapioficeandfire.com/api/books';

    const fetchBooks = async () => {
        SetIsLoading(true)
        const res = await fetch(url)
        const data = await res.json()
        SetIsLoading(false)

        return data
    }


    useEffect(() => {
        const getTasks = async () => {
            const tasksFromServer = await fetchBooks()
            setBooks(tasksFromServer)

        }
        getTasks()
    }, [])

    function BookDetail({book}) {
        const params = useParams();
        return (
            <div className="list-book-container">
                <Link to="/" style={{ textDecoration: 'none', background: '#095BEB', color:'#E0EAF5', padding: '5px' }}>Go back</Link>
                <h2>userId is üëâÔ∏è {params.userId}</h2>
                {/* <BookInfo book={params.bookId} /> */}
            </div>
        );
    }

    function openFilter(){
        console.log('opens')
    }

    function Books() {
        return (
            <div className="list-books-main-container">
                {!isLoading ? <div>
                    <div className="list-books-header-container">
                        <h2>Title</h2>


                        <div class="dropdown">
  <button onclick={openFilter} class="dropbtn">Dropdown</button>
  <div id="myDropdown" class="dropdown-content">
    <a href="#about">Ascending</a>
    <a href="#base">Descending</a>
  </div>
</div>
                        <h2>Publisher</h2>
                    </div>
                    <div className="list-books-container">{books.map((book) => (
                        <Link  to={`/books/${book.name}`} className="list-books-element" style={{ textDecoration: 'none' }}>
                            <Book book={book} />
                        </Link>
                    ))}
                    </div>
                </div> : <h1>Loading...</h1>}
            </div>
        )
    }


    return (
        <div>
            <Routes>
                <Route path="/" element={<Books />} />
                <Route path="/books/:userId" element={<BookDetail />} />
            </Routes>
        </div>

    );
}

export default ListBooks;
